import React, { useState, useEffect  } from 'react';
import axios from 'axios';
import Swal from 'sweetalert2';
import withReactContent from 'sweetalert2-react-content';
import 'bootstrap/dist/css/bootstrap.min.css';

function ExerciseForm({ exerciseToEdit, refreshExercises, clearEdit }) {
    const [idEjercicio,setId] = useState("");
    const [nombre, setNombre] = useState("");
    const [descripcion, setDescripcion] = useState("");
    const [imagenReferencia, setImagen] = useState(null);
    const MySwal = withReactContent(Swal);

  useEffect(() => {
    console.log(exerciseToEdit);
    if (exerciseToEdit) {   
      setId(exerciseToEdit.id_ejericios);
      setNombre(exerciseToEdit.nombreEjecicio);
      setDescripcion(exerciseToEdit.descripcion);
      setImagen(null);
    } else {
      limpiarFormulario();
    }
  }, [exerciseToEdit]); // Dependencia de useEffect
  
  const limpiarFormulario = () => {
    setId("")
    setNombre("");
    setDescripcion("");
    setImagen(null);
  };

  const handleImageChange = (e) => {
    setImagen(e.target.files[0]); // Store the file object, not the string
};



  const handleSubmit = () => {

    const formData = new FormData();
    formData.append('NombreEjercicio', nombre);
    formData.append('Descripcion', descripcion);
    if (imagenReferencia) {
        formData.append('ImagenReferencial', imagenReferencia);
    }

    const config = {
      headers: {
          'Content-Type': 'multipart/form-data'
      }
    };

    if (exerciseToEdit) {
      // Usar PUT para actualizar un ejercicio existente

      axios.put(`https://localhost:7165/api/Ejercicio/${exerciseToEdit.id_ejericio}`,formData,config)
        .then((response) => {
          MySwal.fire({
            icon: 'success',
            title: `<a>Se ha modificado el ejercicio :<strong> ${response.data.myObject.nombreEjecicio} </strong> correctamente</a>`,
            showConfirmButton: false,
            timer: 2000
          });
          refreshExercises();
          limpiarFormulario();
        })
        .catch(error => {
          console.error('Error:', error);
          Swal.fire('Error', 'Ha ocurrido un error al procesar tu solicitud', 'error');
        });
    } else {
      // Usar POST para crear un nuevo ejercicio


      axios.post('https://localhost:7165/api/Ejercicio',formData,config)
        .then((response) => {
          MySwal.fire({
            icon: "success",
            title: `<a>Se ha grabado  el ejercicio :<strong> ${response.data.myObject.nombreEjecicio} </strong> correctamente</a>`,
            showConfirmButton: false,
            timer: 2500
          });
          refreshExercises();
          limpiarFormulario();
        })
        .catch(error => {
            if (error.response) {
                // La solicitud fue hecha y el servidor respondió con un código de estado
                // que no está en el rango de 2xx
                console.error("Datos del error:", error.response.data);
                console.error("Código de estado del error:", error.response.status);
                console.error("Headers del error:", error.response.headers);
            } else if (error.request) {
                // La solicitud fue hecha pero no se recibió respuesta
                console.error("Error de solicitud:", error.request);
            } else {
                // Algo causó un error en la creación de la solicitud
                console.error('Error', error.message);
            }
            console.error("Configuración de la solicitud:", error.config);
          Swal.fire('Error', 'Ha ocurrido un error al procesar tu solicitud', 'error');
        });
    }
  };
  

  return (
    <div className="container mt-4">
      <div className="mb-3 col">
        <input value={nombre} onChange={e => setNombre(e.target.value)} placeholder="Nombre" />
      </div>
      <div className="mb-3 col">
        <input value={descripcion} onChange={e => setDescripcion(e.target.value)} placeholder="Descripción" />
      </div>
      <div className="mb-3 col">
        <input type='file'  value={imagenReferencia} onChange={(e)=>handleImageChange(e.target.value)} placeholder="Imagen Referencial" />
      </div>
      <div>
        <button className='btn btn-success' onClick={handleSubmit}>{exerciseToEdit ? 'Editar' : 'Agregar'}</button>
        {exerciseToEdit && <button className='btn btn-danger' onClick={clearEdit}>Cancelar</button>}
      </div>
    </div>
  );
}

export default ExerciseForm;
