import React, { useState, useEffect  } from 'react';
import axios from 'axios';
import Swal from 'sweetalert2';
import withReactContent from 'sweetalert2-react-content';
import 'bootstrap/dist/css/bootstrap.min.css';

function ExerciseForm({ exerciseToEdit, refreshExercises, clearEdit }) {
    const [idEjercicio,setId] = useState("");
    const [nombre, setNombre] = useState("");
    const [descripcion, setDescripcion] = useState("");
    const [imagenReferencia, setImagen] = useState("");
    const MySwal = withReactContent(Swal);

  useEffect(() => {
    console.log(exerciseToEdit);
    if (exerciseToEdit) {   
      setId(exerciseToEdit.id_ejericios);
      setNombre(exerciseToEdit.nombreEjecicio);
      setDescripcion(exerciseToEdit.descripcion);
      setImagen(exerciseToEdit.imagenReferencial);
    } else {
      limpiarFormulario();
    }
  }, [exerciseToEdit]); // Dependencia de useEffect
  
  const limpiarFormulario = () => {
    setId("")
    setNombre("");
    setDescripcion("");
    setImagen("");
  };

  const handleSubmit = () => {
    if (exerciseToEdit) {
      // Usar PUT para actualizar un ejercicio existente
      console.log(idEjercicio,nombre,descripcion,imagenReferencia);
      console.log({ id_ejericio: exerciseToEdit.id_ejericio, NombreEjercicio: nombre, Descripcion: descripcion, ImagenReferencial: imagenReferencia });

      axios.put(`https://localhost:7165/api/Ejercicio/${exerciseToEdit.id_ejericio}`, { id_ejericio: idEjercicio, NombreEjercicio: nombre, Descripcion: descripcion, ImagenReferencial: imagenReferencia })
        .then(() => {
          MySwal.fire({
            icon: 'success',
            title: 'Operación Exitosa!',
            showConfirmButton: false,
            timer: 1500
          });
          refreshExercises();
          limpiarFormulario();
        })
        .catch(error => {
          console.error('Error:', error);
          Swal.fire('Error', 'Ha ocurrido un error al procesar tu solicitud', 'error');
        });
    } else {
      // Usar POST para crear un nuevo ejercicio
      console.log(idEjercicio,nombre,descripcion,imagenReferencia);
      console.log({  NombreEjercicio: nombre, Descripcion: descripcion, ImagenReferencial: imagenReferencia });

      axios.post('https://localhost:7165/api/Ejercicio', { NombreEjercicio: nombre, Descripcion: descripcion, ImagenReferencial: imagenReferencia })
        .then(() => {
          MySwal.fire({
            icon: 'success',
            title: 'Operación Exitosa!',
            showConfirmButton: false,
            timer: 1500
          });
          refreshExercises();
          limpiarFormulario();
        })
        .catch(error => {
            if (error.response) {
                // La solicitud fue hecha y el servidor respondió con un código de estado
                // que no está en el rango de 2xx
                console.error("Datos del error:", error.response.data);
                console.error("Código de estado del error:", error.response.status);
                console.error("Headers del error:", error.response.headers);
            } else if (error.request) {
                // La solicitud fue hecha pero no se recibió respuesta
                console.error("Error de solicitud:", error.request);
            } else {
                // Algo causó un error en la creación de la solicitud
                console.error('Error', error.message);
            }
            console.error("Configuración de la solicitud:", error.config);
          Swal.fire('Error', 'Ha ocurrido un error al procesar tu solicitud', 'error');
        });
    }
  };
  

  return (
    <div className="container mt-4">
      <div className="mb-3 col">
        <input value={nombre} onChange={e => setNombre(e.target.value)} placeholder="Nombre" />
      </div>
      <div className="mb-3 col">
        <input value={descripcion} onChange={e => setDescripcion(e.target.value)} placeholder="Descripción" />
      </div>
      <div className="mb-3 col">
        <input value={imagenReferencia} onChange={e => setImagen(e.target.value)} placeholder="Imagen Referencial" />
      </div>
      <div>
        <button className='btn btn-success' onClick={handleSubmit}>{exerciseToEdit ? 'Editar' : 'Agregar'}</button>
        {exerciseToEdit && <button className='btn btn-danger' onClick={clearEdit}>Cancelar</button>}
      </div>
    </div>
  );
}

export default ExerciseForm;
