import React, { useState } from 'react';
import axios from 'axios';
import Swal from 'sweetalert2';
import withReactContent from 'sweetalert2-react-content';
import 'bootstrap/dist/css/bootstrap.min.css';

function ExerciseForm({ refreshExercises, exerciseToEdit, clearEdit }) {
  const [nombre, setNombre] = useState(exerciseToEdit ? exerciseToEdit.nombre : "");
  const [descripcion, setDescripcion] = useState(exerciseToEdit ? exerciseToEdit.descripcion : "");
  const [imagenReferencia, setImagen] = useState(exerciseToEdit ? exerciseToEdit.imagenReferencial : "");
  const MySwal = withReactContent(Swal);

  const handleSubmit = () => {
    const url = exerciseToEdit ? `https://localhost:7165/api/Ejercicio/${exerciseToEdit.id}` : 'https://localhost:7165/api/Ejercicio';
    const method = exerciseToEdit ? 'put' : 'post';
    axios[method](url, { NombreEjercicio: nombre, Descripcion: descripcion, ImagenReferencial: imagenReferencia })
      .then(() => {
        MySwal.fire({
          icon: 'success',
          title: 'Operación Exitosa!',
          showConfirmButton: false,
          timer: 1500
        });
        refreshExercises();
        clearEdit();
      })
      .catch(error => {
        console.error('Error:', error);
        Swal.fire('Error', 'Ha ocurrido un error al procesar tu solicitud', 'error');
      });
  };

  return (
  
        <div className="container mt-4">
            <div className="mb-3 col">
                <input value={nombre} onChange={e => setNombre(e.target.value)} placeholder="Nombre" />
            </div>
            <div className="mb-3 col">
                <input value={descripcion} onChange={e => setDescripcion(e.target.value)} placeholder="Descripción" />
                </div>
                <div className="mb-3 col">
                <input value={imagenReferencia} onChange={e => setImagen(e.target.value)} placeholder="Imagen Referencial" />
                </div>
                <div>
                <button  className='btn btn-success' onClick={handleSubmit}>{exerciseToEdit ? 'Editar' : 'Agregar'}</button>
                {exerciseToEdit && <button  className='btn btn-danger' onClick={clearEdit}>Cancelar</button>}
                </div>

            
           
      </div>

      
      
  );
}

export default ExerciseForm;
