import React, { useState } from 'react'
import Form from 'react-bootstrap/Form'
import Ejercicios from './Ejercicios';
import NuevoEjercicio from './AgregarNuevoEjercicioRutina';
import { Button } from 'bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

function RutinaForm() {
    const [idRutina,setIdRutina] = useState(0);
    const [nombreRutina,setNombreRutina] = useState("");
    const [arrayDetalleEjercicios,setArrayDetalleEjercicios] = useState([]);
    const [showAgregarEjercicio, setShowAgregarEjercicio] = useState(false);

const AgregarDetalleEjercicio = (detalleEjercicio) =>{ 
    setArrayDetalleEjercicios(prevDetalleEjercicios => {
        const newEjercicios = [...prevEjercicios, ejercicio];
        console.log(newEjercicios); // Verifica qué está siendo agregado
        return newEjercicios;
    });
    
}


  return (
    <div className='container mt-4'>
    <Form>
    <Form.Group className="mb-2" >
                    <Form.Label>Nombre de La Rutina </Form.Label>
                    <Form.Control value={nombreRutina} onChange={e => setNombreRutina(e.target.value)} type="text" placeholder="Ingrese el Nombre de La Rutina" />
      </Form.Group>
      </Form>
      <button className='btn btn-success' onClick={() => setShowAgregarEjercicio(true) }>{'Agregar Ejercicio'}</button>
      {showAgregarEjercicio && <NuevoEjercicio AgregarDetalleEjercicio={AgregarDetalleEjercicio} />}

      <button className='btn btn-success' onClick={() => console.log(arrayDetalleEjercicios) }>{'Ver lista'}</button>
    </div>
  )
}

export default RutinaForm
